import React from "react";
import { useNavigate } from "react-router-dom";

const StartPage = () => {
  const navigate = useNavigate();

  const handleSelect = (option) => {
    if (option === "verify") {
      navigate("/news-verify");
    } else if (option === "news") {
      navigate("/homepage");
    }
  };

  return (
    <div className="max-w-5xl mx-auto mt-16 text-center">
      <h2 className="text-3xl font-bold mb-2 text-gray-800">Welcome to the News Portal</h2>
      <p className="text-lg text-gray-600 mb-8">Please select an option:</p>
      <div className="flex flex-row gap-10 justify-center mt-10">
        {/* News Verify Card */}
        <div
          className="w-80 bg-white rounded-xl shadow-lg p-6 flex flex-col items-center justify-between min-h-[340px] transition-transform duration-200 hover:scale-105 hover:shadow-2xl border-2 border-transparent hover:border-blue-500 cursor-pointer"
          onClick={() => handleSelect("verify")}
        >
          <div>
            <div className="w-44 h-28 bg-gray-200 rounded-lg mx-auto mb-5 flex items-center justify-center overflow-hidden">
              <img
                src="https://r2.erweima.ai/i/A1ctOnndRcCOygOLsDqxyA.png"
                alt="Verify"
                className="w-full h-full object-cover rounded-lg"
              />
            </div>
            <h3 className="text-xl font-semibold mb-2 text-blue-700">News Verify</h3>
            <p className="text-gray-600">
              Check the authenticity of news articles and fight misinformation.
            </p>
          </div>
          <button
            className="mt-6 px-8 py-2 text-lg rounded-md bg-blue-600 hover:bg-blue-700 text-white font-medium shadow transition"
            onClick={e => { e.stopPropagation(); handleSelect("verify"); }}
          >
            Verify News
          </button>
        </div>
        {/* News Card */}
        <div
          className="w-80 bg-white rounded-xl shadow-lg p-6 flex flex-col items-center justify-between min-h-[340px] transition-transform duration-200 hover:scale-105 hover:shadow-2xl border-2 border-transparent hover:border-green-500 cursor-pointer"
          onClick={() => handleSelect("news")}
        >
          <div>
            <div className="w-44 h-28 bg-gray-200 rounded-lg mx-auto mb-5 flex items-center justify-center overflow-hidden">
              <img
                src="https://townsquare.media/site/99/files/2021/12/attachment-news-story-of-the-year.jpg?w=1200&h=0&zc=1&s=0&a=t&q=89"
                alt="News"
                className="w-full h-full object-cover rounded-lg"
              />
            </div>
            <h3 className="text-xl font-semibold mb-2 text-green-700">News</h3>
            <p className="text-gray-600">
              Read the latest news articles and stay updated with current events.
            </p>
          </div>
          <button
            className="mt-6 px-8 py-2 text-lg rounded-md bg-green-600 hover:bg-green-700 text-white font-medium shadow transition"
            onClick={e => { e.stopPropagation(); handleSelect("news"); }}
          >
            News
          </button>
        </div>
      </div>
    </div>
  );
};

export default StartPage;
