import React from 'react';
import './LoadingSpinner.css'; 

function LoadingSpinner() {
  return (
    <div className="spinner-container">
      <div className="loading-spinner"></div>
      <p>Loading...</p> {/* Optional text */}
    </div>
  );
}

export default LoadingSpinner;
