// src/components/common/Header.js
import React from "react";
import { Link } from 'react-router-dom'; // Import Link for client-side navigation
import "./Header.css";

function Header() {
  // Ensure category names match what NewsAPI expects (usually lowercase)
  const categories = [
    { name: "Sports", pathValue: "sports" },
    { name: "Politics", pathValue: "politics" }, // NewsAPI might not have 'politics', common categories are business, entertainment, general, health, science, sports, technology
    { name: "Entertainment", pathValue: "entertainment" },
    { name: "Technology", pathValue: "technology" },
    { name: "Health", pathValue: "health" },
    { name: "Business", pathValue: "business" },
    { name: "Science", pathValue: "science" },
  ];

  return (
    <header className="app-header">
      <p className="logo">
          {/* Make logo a link to home */}
          <Link to="/">InfoCrucible</Link>
      </p>
      <nav>
        <ul>
          {/* Use Link component for internal navigation */}
          <li><Link to="/">Home</Link></li>

          {/* Dropdown Category Menu */}
          <li className="dropdown">
            <button type="button" className="dropbtn" aria-haspopup="true">
              Category â–¾
            </button>
            <ul className="dropdown-menu">
              {categories.map((category) => (
                <li key={category.pathValue}>
                  {/* === Use Link component and construct the correct path === */}
                  <Link to={`/category/${category.pathValue}`}>
                    {category.name}
                  </Link>
                  {/* ====================================================== */}
                </li>
              ))}
            </ul>
          </li>

          <li><Link to="/about">About</Link></li>
          {/* Add Link for News if it's a separate page */}
          {/* <li><Link to="/news">News</Link></li> */}
        </ul>
      </nav>
    </header>
  );
}

export default Header;
