import React from "react";
import { useNavigate } from "react-router-dom";

const StartPage = () => {
  const navigate = useNavigate();

  const handleSelect = (option) => {
    if (option === "verify") {
      navigate("/news-verify");
    } else if (option === "news") {
      navigate("/news");
    }
  };

  return (
    <div style={{ maxWidth: 900, margin: "60px auto", textAlign: "center" }}>
      <h2>Welcome to the News Portal</h2>
      <p>Please select an option:</p>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          gap: 40,
          justifyContent: "center",
          marginTop: 40,
        }}
      >
        {/* News Verify Card */}
        <div
          style={{
            width: 300,
            background: "#f8f8f8",
            borderRadius: 12,
            boxShadow: "0 2px 8px #eee",
            padding: 24,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "space-between",
            minHeight: 320,
          }}
        >
          <div>
            <div
              style={{
                width: 180,
                height: 110,
                background: "#e0e0e0",
                borderRadius: 10,
                margin: "0 auto 18px auto",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                color: "#888",
                fontSize: 18,
              }}
            >
              {/* Replace with image if available */}
              <img
                src="https://r2.erweima.ai/i/A1ctOnndRcCOygOLsDqxyA.png"
                alt="Verify"
                style={{ width: "100%", height: "auto", borderRadius: 10 }}
              />
            </div>
            <h3>News Verify</h3>
            <p style={{ color: "#666" }}>
              Check the authenticity of news articles and fight misinformation.
            </p>
          </div>
          <button
            style={{
              marginTop: 24,
              padding: "10px 32px",
              fontSize: 16,
              cursor: "pointer",
              borderRadius: 6,
              border: "none",
              background: "#1976d2",
              color: "#fff",
            }}
            onClick={() => handleSelect("verify")}
          >
            Verify News
          </button>
        </div>
        {/* News Card */}
        <div
          style={{
            width: 300,
            background: "#f8f8f8",
            borderRadius: 12,
            boxShadow: "0 2px 8px #eee",
            padding: 24,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "space-between",
            minHeight: 320,
          }}
        >
          <div>
            <div
              style={{
                width: 180,
                height: 110,
                background: "#e0e0e0",
                borderRadius: 10,
                margin: "0 auto 18px auto",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                color: "#888",
                fontSize: 18,
              }}
            > <img
              src="https://th.bing.com/th/id/OIP.tdE6TlSUhbws8F66R19rmQHaE8?r=0&rs=1&pid=ImgDetMain"
              alt="News"
              style={{ width: "100%", height: "auto", borderRadius: 10 }}
            />
            </div>
            <h3>News</h3>
            <p style={{ color: "#666" }}>
              Read the latest news articles and stay updated with current events.
            </p>
          </div>
          <button
            style={{
              marginTop: 24,
              padding: "10px 32px",
              fontSize: 16,
              cursor: "pointer",
              borderRadius: 6,
              border: "none",
              background: "#388e3c",
              color: "#fff",
            }}
            onClick={() => handleSelect("news")}
          >
             News
          </button>
        </div>
      </div>
    </div>
  );
};

export default StartPage;
