import React from "react";
import { useNavigate } from "react-router-dom";

const StartPage = () => {
  const navigate = useNavigate();

  const handleSelect = (option) => {
    if (option === "verify") {
      navigate("/news-verify");
    } else if (option === "news") {
      navigate("/news");
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "60px auto", textAlign: "center" }}>
      <h2>Welcome to the News Portal</h2>
      <p>Please select an option:</p>
      {/* Space for image */}
      <div style={{ margin: "30px 0" }}>
        <div
          style={{
            width: 200,
            height: 120,
            margin: "0 auto",
            background: "#eee",
            borderRadius: 10,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "#888",
            fontSize: 18,
          }}
        >
          Image Here
        </div>
      </div>
      {/* Horizontal buttons */}
      <div style={{ display: "flex", flexDirection: "row", gap: 40, justifyContent: "center", marginTop: 30 }}>
        <button
          style={{ padding: "12px 32px", fontSize: 18, cursor: "pointer" }}
          onClick={() => handleSelect("verify")}
        >
          News Verify
        </button>
        <button
          style={{ padding: "12px 32px", fontSize: 18, cursor: "pointer" }}
          onClick={() => handleSelect("news")}
        >
          News
        </button>
      </div>
    </div>
  );
};

export default StartPage;
