import React from "react";
import { useNavigate } from "react-router-dom";

const StartPage = () => {
  const navigate = useNavigate();

  const handleSelect = (option) => {
    if (option === "verify") {
      navigate("/news-verify");
    } else if (option === "news") {
      navigate("/news");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-16 px-4 text-center">
      {/* Hero image / logo */}
      <img
        src="/assets/news-hero.png"              // ↪️ replace with your image path
        alt="News Portal Hero"
        className="w-32 h-32 mx-auto mb-8 object-contain"
      />

      <h2 className="text-2xl font-bold mb-2">Welcome to the News Portal</h2>
      <p className="text-gray-600 mb-6">Please select an option:</p>

      <div className="space-y-4">
        <button
          type="button"
          className="w-full py-3 text-lg font-medium rounded-lg bg-blue-600 text-white hover:bg-blue-700 transition"
          onClick={() => handleSelect("verify")}
        >
          News Verify
        </button>
        <button
          type="button"
          className="w-full py-3 text-lg font-medium rounded-lg bg-green-600 text-white hover:bg-green-700 transition"
          onClick={() => handleSelect("news")}
        >
          News
        </button>
      </div>
    </div>
  );
};

export default StartPage;
