import React, { useEffect, useState } from 'react';
import './Hero.css';

// Dummy fallback trending news (in case API fails)
const fallbackTrending = [
  {
    title: "India wins historic cricket match!",
    image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1200&q=80",
    url: "#"
  },
  {
    title: "Tech giants announce new AI breakthrough",
    image: "https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=1200&q=80",
    url: "#"
  },
  {
    title: "Global markets rally after positive news",
    image: "https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=1200&q=80",
    url: "#"
  }
];

// Use your news studio background image
const backgroundUrl = "/news-studio-bg.jpg"; // Place your background image in public folder and use this path

function Hero() {
  const [trending, setTrending] = useState([]);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const fetchTrending = async () => {
      try {
        const apiKey = process.env.REACT_APP_NEWS_API_KEY;
        const url = `https://newsdata.io/api/1/latest?apikey=${apiKey}&country=us&language=en&category=top`;
        const res = await fetch(url);
        const data = await res.json();
        if ((data.status === "success" || data.status === "ok") && data.results) {
          const valid = data.results
            .filter(a => a.title && a.image_url)
            .slice(0, 5)
            .map(a => ({
              title: a.title,
              image: a.image_url,
              url: a.link
            }));
          setTrending(valid.length ? valid : fallbackTrending);
        } else {
          setTrending(fallbackTrending);
        }
      } catch {
        setTrending(fallbackTrending);
      }
    };
    fetchTrending();
  }, []);

  // Auto-scroll every 4 seconds
  useEffect(() => {
    if (trending.length < 2) return;
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % trending.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [trending]);

  if (!trending.length) return null;

  return (
    <div
      className="hero-studio-bg"
      style={{
        width: "100vw",
        minHeight: 350,
        background: `url(${backgroundUrl}) center/cover no-repeat`,
        position: "relative",
        overflow: "hidden",
        left: "50%",
        right: "50%",
        marginLeft: "-50vw",
        marginRight: "-50vw",
        display: "flex",
        alignItems: "center",
        justifyContent: "center"
      }}
    >
      {/* News image and headline carousel */}
      <div
        className="hero-news-carousel"
        style={{
          position: "absolute",
          left: "50%",
          top: "60%",
          transform: "translate(-50%, -50%)",
          width: 480,
          maxWidth: "90vw",
          background: "rgba(0,0,0,0.7)",
          borderRadius: 18,
          boxShadow: "0 4px 24px #0008",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          padding: "24px 24px 18px 24px",
          zIndex: 2,
          transition: "all 0.5s"
        }}
      >
        <div
          style={{
            width: "100%",
            height: 180,
            borderRadius: 12,
            overflow: "hidden",
            marginBottom: 18,
            boxShadow: "0 2px 12px #0006",
            background: "#222"
          }}
        >
          <img
            src={trending[current].image}
            alt={trending[current].title}
            style={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              display: "block"
            }}
          />
        </div>
        <h2
          style={{
            color: "#fff",
            fontSize: "1.35rem",
            fontWeight: 600,
            textAlign: "center",
            marginBottom: 8,
            textShadow: "0 2px 8px #000a"
          }}
        >
          {trending[current].title}
        </h2>
        <a
          href={trending[current].url}
          target="_blank"
          rel="noopener noreferrer"
          style={{
            color: "#90caf9",
            fontWeight: 500,
            fontSize: 16,
            textDecoration: "underline",
            marginTop: 6
          }}
        >
          Read Full Story
        </a>
        <div className="hero-dots" style={{ marginTop: 12 }}>
          {trending.map((_, idx) => (
            <span
              key={idx}
              className={`dot${idx === current ? " active" : ""}`}
              onClick={() => setCurrent(idx)}
              style={{
                width: 12,
                height: 12,
                borderRadius: "50%",
                background: idx === current ? "#1976d2" : "#fff7",
                border: "2px solid #1976d2",
                cursor: "pointer",
                margin: "0 4px",
                display: "inline-block"
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Hero;
