import React, { useState } from "react";

const NewsVerify = () => {
  const [text, setText] = useState("");
  const [file, setFile] = useState(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would send `text` and `file` to your backend for verification
    alert("Submitted for verification!");
  };

  return (
    <div style={{ maxWidth: 500, margin: "40px auto", padding: 24, border: "1px solid #ccc", borderRadius: 10 }}>
      <h2>Verify News</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: 18 }}>
          <label>
            Enter News Text:
            <textarea
              value={text}
              onChange={handleTextChange}
              rows={5}
              style={{ width: "100%", marginTop: 8, padding: 8 }}
              placeholder="Paste or type news text here..."
            />
          </label>
        </div>
        <div style={{ marginBottom: 18 }}>
          <label>
            Or Upload a File:
            <input
              type="file"
              accept=".txt,.pdf,.doc,.docx"
              onChange={handleFileChange}
              style={{ display: "block", marginTop: 8 }}
            />
          </label>
          {file && <div style={{ marginTop: 8 }}>Selected file: {file.name}</div>}
        </div>
        <button type="submit" style={{ padding: "10px 28px", fontSize: 16 }}>
          Verify
        </button>
      </form>
    </div>
  );
};

export default NewsVerify;
